import React, { useState } from 'react';
import img1 from './img/img2.png';
import "../index.css";
const getLocalItems = () =>{
  let list = localStorage.getItem('lists');
  console.log(list);

  if (list){
    return JSON.parse(localStorage.getItem('lists'));
  } else {
    return [];
  }
}

const Todo = () => {
  const [inputData, setinputData] = useState('');
  const [items, setItems] = useState([]);

  const addItem = () => {
    if (!inputData) {
      alert("Please enter an item");  // Provide feedback if input is empty
    } else {
      const allInputData = {id : new Date().getTime().toString(), name:inputData}
      setItems([...items, allInputData]);
      setinputData('');
    }
  }

  const deleteItem = (id) => {
    const updatedItems = items.filter((elem, index) => index !== id);  // Correct the variable name to ind
    setItems(updatedItems);
  }

  const editItem = (id) =>{
    let newEditItem = items.find((elem) =>{
      return elem.id === id
    })
    console.log(newEditItem);
  }
  const removeAll = () => {
    setItems([]);
  }

  return (
    <div>
      <div className="main-div">
        <div className="child-div">
          <figure>
            <img src={img1} alt="todologo" />
            <figcaption>Add Your List Here</figcaption>
          </figure>

          <div className="addItems">
            <input 
              type="text" 
              placeholder='✍️ Add Items' 
              value={inputData} 
              onChange={(e) => setinputData(e.target.value)} 
            />
          
            <i className='fa fa-plus add-btn' title='Add Item' onClick={addItem}></i>
          </div>

          <div className='showItems'>
            {items.map((elem, id) => (
              <div className="eachItem" key={elem.id}>
                <h3>{elem.name}</h3>
                <i className='far fa-trash-alt add-btn' title='Delete Item' onClick={() => editItem(elem.id)}></i>
              </div>
            ))}
          </div>

          <div className='showItems'>
            {items.map((elem, id) => (
              <div className="eachItem" key={elem.id}>
                <h3>{elem.name}</h3>
                <i className='far fa-trash-alt add-btn' title='Delete Item' onClick={() => deleteItem(elem.id)}></i>
              </div>
            ))}
          </div>

          {/* Clear all button */}
          <div className="showItems">
            <button className="btn effect04" data-sm-link-text="Remove All" onClick={removeAll}>
              <span>Check List</span>
            </button>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Todo;
