  }

  const removAll = () =>{
    setItems([]);
  }
  return (
    <div>
      <div className="main-div">
        <div className="child-div">
          <figure>
            <img src={img1} alt="todologo" />
            <figcaption>Add Your List Here</figcaption>
          </figure>
          <div className="addItems">
            <input type="text" placeholder='✍️ Add Items' value={inputData} onChange={(e) => setinputData(e.target.value)} />
            <i className='fa fa-plus add-btn' title='Add Item' onClick={addItem}></i>
          </div>

          <div className='showItems'>
            {
              items.map((elem, ind) => {
                return (
                  <div className="eachItem" key={ind}>
                    <h3>{elem}</h3>
                    <i className='far fa-trash-alt add-btn' title='Delete Item' onClick={() => deleteItem(ind)}></i>
                  </div>
                )
              })
            }

          </div>

          {/* clear all button */}
          <div className="showItems">
            <button className="btn effect04" data-sm-link-text="Remove All" onClick={removAll}><span>Check List</span></button>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Todo
