import React, { useState } from 'react';
import img1 from './img/img1.jpeg';
import "../index.css";

const Todo = () => {
  const [inputData, setinputData] = useState('');
  const [items, setItems] = useState([]);

  const addItem = () => {
    if (!inputData) {
      alert("Please enter an item");  // Provide feedback if input is empty
    } else {
      setItems([...items, inputData]);
      setinputData('');
    }
  }

  const deleteItem = (ind) => {
    const updatedItems = items.filter((elem, index) => index !== ind);  // Correct the variable name to ind
    setItems(updatedItems);
  }

  const removeAll = () => {
    setItems([]);
  }

  return (
    <div>
      <div className="main-div">
        <div className="child-div">
          <figure>
            <img src={img1} alt="todologo" />
            <figcaption>Add Your List Here</figcaption>
          </figure>

          <div className="addItems">
            <input 
              type="text" 
              placeholder='✍️ Add Items' 
              value={inputData} 
              onChange={(e) => setinputData(e.target.value)} 
            />
            <i className='fa fa-plus add-btn' title='Add Item' onClick={addItem}></i>
          </div>

          <div className='showItems'>
            {items.map((elem, ind) => (
              <div className="eachItem" key={ind}>
                <h3>{elem}</h3>
                <i className='far fa-trash-alt add-btn' title='Delete Item' onClick={() => deleteItem(ind)}></i>
              </div>
            ))}
          </div>

          {/* Clear all button */}
          <div className="showItems">
            <button className="btn effect04" data-sm-link-text="Remove All" onClick={removeAll}>
              <span>Check List</span>
            </button>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Todo;
